import * as React from 'react';


import { request, setAuthHeader } from './helpers/axios_helper';

import Buttons from './Buttons';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import WelcomeContent from './WelcomeContent'
import Calendar from "./Calendar";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

export default class AppContent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            componentToShow: "welcome"
        }
    };

    calendar = () => {
        this.setState({componentToShow: "calendar"})
    };

    login = () => {
        this.setState({componentToShow: "login"})
    };

    logout = () => {
        this.setState({componentToShow: "welcome"})
        setAuthHeader(null);
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request(
            "POST",
            "/login",
            {
                login: username,
                password: password
            }).then(
            (response) => {
                setAuthHeader(response.data.token);
                this.setState({componentToShow: "messages"});
            }).catch(
            (error) => {
                setAuthHeader(null);
                this.setState({componentToShow: "welcome"})
            }
        );
    };

    onRegister = (event, firstName, lastName, username, password) => {
        event.preventDefault();
        request(
            "POST",
            "/register",
            {
                firstName: firstName,
                lastName: lastName,
                login: username,
                password: password
            }).then(
            (response) => {
                setAuthHeader(response.data.token);
                this.setState({componentToShow: "messages"});
            }).catch(
            (error) => {
                setAuthHeader(null);
                this.setState({componentToShow: "welcome"})
            }
        );
    };

    render() {
        return (
            <Router>
                <>
                    <Buttons calendar={this.calendar} login={this.login} logout={this.logout} />
                    <Switch>
                        <Route path="/" exact component={WelcomeContent} />
                        <Route path="/login" render={(props) => <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} {...props} />} />
                        <Route path="/calendar/:username" render={(props) => <Calendar {...props} />} />
                        {/* Add other routes as needed */}
                    </Switch>
                </>
            </Router>
        );
    }
}
